<div class="container">
  <div class="row">
    <div id="data" class="panel grey darken-4 col s12">
      <div class="row center no-margin">
        <div class="col s3">
          Uridium: 75.000
        </div>
        <div class="col s3">
          Credits: 75.000
        </div>
        <div class="col s3">
          Honor: 75.000
        </div>
        <div class="col s3">
          Experience: 75.000
        </div>
      </div>
    </div>

    <div id="profile" class="panel grey darken-4 col s12">
      <img src="/img/avatar.png">
      <div class="profile-informations">
        <h5>{{ shipName }}</h5>
        <p>Clan: [ADM] Administrators</p>
        <p>Rank: <img src="/img/rank_20.png"> General</p>
        <p>ID: 1</p>
      </div>
    </div>

    <div id="hall-of-fame" class="panel grey darken-4 center col s12">
      <h5 >HALL OF FAME</h5>
      <ul class="tabs grey darken-3 tabs-fixed-width tab-demo z-depth-1">
        <li class="tab"><a href="#pilots">PILOTS</a></li>
        <li class="tab"><a href="#clans">CLANS</a></li>
      </ul>
      <div id="pilots" class="col s12">
        <table class="striped highlight">
          <thead>
            <tr>
              <th>Name</th>
              <th>Company</th>
              <th>Rank</th>
              <th>Value</th>
            </tr>
          </thead>

          <tbody>
            <tr>
              <td>Legionary</td>
              <td><img src="/img/logo_eic_mini.png"></td>
              <td>1</td>
              <td>134234234</td>
            </tr>
          </tbody>
        </table>
      </div>
      <div id="clans" class="col s12">
        <table class="striped highlight">
          <thead>
            <tr>
              <th>Name</th>
              <th>Rank</th>
              <th>Value</th>
            </tr>
          </thead>

          <tbody>
            <tr>
              <td>[ADM] Administrators</td>
              <td>1</td>
              <td>134234234</td>
            </tr>
          </tbody>
        </table>
      </div>
    </div>
  </div>
</div>
