<div class="container container-center">
  <div class="row form">
    <div class="col s6 offset-s3">
      <div class="card">

        <ul class="tabs grey darken-3 tabs-fixed-width tab-demo z-depth-1">
          <li class="tab"><a href="#login">LOG IN</a></li>
          <li class="tab"><a href="#register">REGISTER</a></li>
        </ul>

        <div id="login">
          <div class="card-content">
            <div class="row">
              <div class="input-field col s12">
                <i class="material-icons prefix">person</i>
                <input type="text" id="username">
                <label for="username">Username</label>
              </div>
            </div>
            <div class="row">
              <div class="input-field col s12">
                <i class="material-icons prefix">lock</i>
                <input type="password" id="password">
                <label for="password">Password</label>
              </div>
            </div>
            <div class="row">
              <div class="input-field col s12">
                <button id="login-button" class="btn grey darken-3 waves-effect waves-light col s12">LOG IN</button>
              </div>
            </div>
          </div>
        </div>

        <div id="register">
          <div class="card-content">
            <div class="row">
              <div class="input-field col s12">
                <i class="material-icons prefix">person</i>
                <input type="text" id="username">
                <label for="username">Username</label>
              </div>
            </div>
            <div class="row">
              <div class="input-field col s12">
                <i class="material-icons prefix">lock</i>
                <input type="email" id="email">
                <label for="email">Email</label>
              </div>
            </div>
            <div class="row">
              <div class="input-field col s12">
                <i class="material-icons prefix">lock</i>
                <input type="password" id="password">
                <label for="password">Password</label>
              </div>
            </div>
            <div class="row">
              <div class="input-field col s12">
                <button id="login-button" class="btn grey darken-3 waves-effect waves-light col s12">REGISTER</button>
              </div>
            </div>
          </div>
        </div>

        <div class="card-action">
          <a id="reset-password" href="#" class="grey-text text-darken-2">FORGOT PASSWORD</a>
        </div>
      </div>
   </div>
  </div>
</div>
